import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom'; // Import Link from react-router-dom

const ProfilePage = () => {
    const { userId } = useParams();
    const [videos, setVideos] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchVideos = async () => {
          try {
            const response = await axios.get(`http://localhost:7000/api/v1/videos/${userId}`, {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('accessToken')}`
              },
              withCredentials: true
            });
            console.log('API Response:', response.data); // Log the entire API response for debugging
            if (response.data.success) {
              setVideos(response.data.data.videos);
            } else {
              setMessage(response.data.message || 'Failed to fetch videos');
            }
          } catch (error) {
            if (error.response) {
              console.error('Server responded with error:', error.response.data);
            } else if (error.request) {
              console.error('No response received from server:', error.request);
            } else {
              console.error('Error setting up the request:', error.message);
            }
            setMessage('Failed to fetch videos');
          }
        };

        fetchVideos();
      }, [userId]);


    return (
        <div className="max-w-7xl mx-auto mt-10 p-6">
          <h2 className="text-2xl font-bold mb-6 text-center">All Videos</h2>
          {message && <p className="mb-4 text-center text-red-500">{message}</p>}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {videos.length > 0 ? (
              videos.map(video => (
                <Link key={video._id} to={`/video/${video._id}`} className="block group">
                  <div className="relative overflow-hidden rounded-md shadow-lg">
                    <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover transition-transform duration-200 transform group-hover:scale-105" />
                    <div className="p-4">
                      <h3 className="text-lg font-semibold text-gray-900 group-hover:text-indigo-600">{video.title}</h3>
                      <p className="text-sm text-gray-600 truncate">{video.description}</p>
                    </div>
                  </div>
                </Link>
              ))
            ) : (
              <p className="text-center col-span-full">No videos found</p>
            )}
          </div>
        </div>
      );
};

export default ProfilePage;
