import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { Context } from "../context/contextApi";

const VideoDetail = () => {
    const { user } = useContext(Context);
    const { videoId } = useParams();
    const [video, setVideo] = useState(null);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [message, setMessage] = useState('');
    const [liked, setLiked] = useState(false);


    useEffect(() => {
        const fetchVideoDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:7000/api/v1/videos/${videoId}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                    },
                    withCredentials: true
                });
                setVideo(response.data.data);
                if (user && response.data.likes.includes(user._id)) {
                    setLiked(true);
                }
            } catch (error) {
                handleError(error);
            }
        };

        const fetchComments = async () => {
            try {
                const response = await axios.get(`http://localhost:7000/api/v1/comments/${videoId}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                    },
                    withCredentials: true
                });
                setComments(response.data.data || []);
            } catch (error) {
                handleError(error);
            }
        };

        fetchVideoDetails();
        fetchComments();
    }, [videoId, user]);

    const handleError = (error) => {
        if (error.response) {
            console.error('Server responded with error:', error.response.data);
        } else if (error.request) {
            console.error('No response received from server:', error.request);
        } else {
            console.error('Error setting up the request:', error.message);
        }
        setMessage('An error occurred');
    };

    const toggleLike = async () => {
        try {
            await axios.put(`http://localhost:7000/api/v1/likes/toggle/v/${videoId}`, {}, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            setLiked(!liked);
        } catch (error) {
            handleError(error);
        }
    };

    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`http://localhost:7000/api/v1/comments/${videoId}`, {
                text: newComment
            }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            setComments([...comments, response.data.data]);
            setNewComment('');
        } catch (error) {
            handleError(error);
        }
    };

    const handleCommentDelete = async (commentId) => {
        try {
            await axios.delete(`http://localhost:7000/api/v1/comments/c/${commentId}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            setComments(comments.filter(comment => comment._id !== commentId));
        } catch (error) {
            handleError(error);
        }
    };

    const handleCommentUpdate = async (commentId, updatedText) => {
        try {
            const response = await axios.put(`http://localhost:7000/api/v1/comments/${commentId}`, {
                text: updatedText
            }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            setComments(comments.map(comment => comment._id === commentId ? response.data.data : comment));
        } catch (error) {
            handleError(error);
        }
    };

    if (!user) {
        return <p className="text-center">Please log in to see the video details.</p>;
    }

    return (
        <div className="max-w-2xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
            <h2 className="text-2xl font-bold mb-6 text-center">Video Details</h2>
            {message && <p className="mb-4 text-center text-red-500">{message}</p>}
            {video ? (
                <div>
                    <h3 className="text-xl font-semibold">{video.title}</h3>
                    <p>{video.description}</p>
                    <video controls src={video.videoFile} className="w-full mt-2" />
                    <div className="flex items-center mt-4">
                        <button
                            onClick={toggleLike}
                            className={`px-4 py-2 rounded-md text-white ${liked ? 'bg-red-500' : 'bg-gray-500'}`}
                        >
                            {liked ? 'Unlike' : 'Like'}
                        </button>
                    </div>
                    <div className="mt-6">
                        <h4 className="text-lg font-semibold">Comments</h4>
                        <ul className="mt-4">
                            {comments.length > 0 ? (
                                comments.map(comment => (
                                    <li key={comment._id} className="mb-2">
                                        <p><strong>{comment.user.username}:</strong> {comment.content}</p>
                                        <button onClick={() => handleCommentDelete(comment._id)} className="text-red-600 hover:underline">Delete</button>
                                        <button onClick={() => handleCommentUpdate(comment._id, prompt('Update your comment:', comment.content))} className="ml-4 text-blue-600 hover:underline">Edit</button>
                                    </li>
                                ))
                            ) : (
                                <p className="text-center">No comments found</p>
                            )}
                        </ul>
                        <form onSubmit={handleCommentSubmit} className="mt-4">
                            <textarea
                                value={newComment}
                                onChange={(e) => setNewComment(e.target.value)}
                                className="w-full p-2 border rounded-md"
                                placeholder="Add a comment"
                                rows="3"
                            />
                            <button type="submit" className="px-4 py-2 mt-2 rounded-md bg-blue-500 text-white">
                                Submit
                            </button>
                        </form>
                    </div>
                </div>
            ) : (
                <p className="text-center">Loading...</p>
            )}
        </div>
    );
};

export default VideoDetail;
