import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
    const { userId } = useParams();
    const [videos, setVideos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchVideos = async () => {
            const token = localStorage.getItem('accessToken'); // Retrieve the token from local storage or any secure place

            // Check if the token is valid and in the correct format
            if (!token || !token.includes('.')) {
                setError('Invalid token');
                setLoading(false);
                return;
            }

            try {
                const response = await axios.get(`http://localhost:7000/api/v1/videos/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setVideos(response.data.videos);
            } catch (error) {
                if (error.response) {
                    console.error('Server responded with error:', error.response.data);
                    setError('Error fetching videos: ' + error.response.data.message);
                } else if (error.request) {
                    console.error('No response received from server:', error.request);
                    setError('No response received from server');
                } else {
                    console.error('Error setting up the request:', error.message);
                    setError('Error setting up the request: ' + error.message);
                }
            } finally {
                setLoading(false);
            }
        };

        fetchVideos();
    }, [userId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="min-h-screen bg-gray-900 text-white py-5 px-4">
            <h1 className="text-2xl font-bold mb-4">User Videos</h1>
            <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                {videos.map(video => (
                    <li key={video._id} className="bg-gray-800 p-4 rounded-lg shadow-md">
                        <h2 className="text-lg font-semibold mb-2">{video.title}</h2>
                        <video className="w-full h-auto" controls>
                            <source src={video.url} type="video/mp4" />
                            Your browser does not support the video tag.
                        </video>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProfilePage;
