// src/pages/ProfilePage.js
import React, { useContext, useEffect, useState } from 'react';

import axios from 'axios';
import { Context } from '../context/contextApi';

const ProfilePage = () => {
  const { user } = useContext(Context);
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    if (user) {
      axios.get(`/api/v1/users/${user.id}/profile`)
        .then(response => setProfileData(response.data))
        .catch(error => console.error('Error fetching profile data:', error));
    }
  }, [user]);

  if (!user) {
    return <p className="text-center mt-10">You need to log in to view this page.</p>;
  }

  if (!profileData) {
    return <p className="text-center mt-10">Loading...</p>;
  }

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <div className="flex items-center space-x-4">
        <img src={profileData.avatar} alt="Profile" className="w-24 h-24 rounded-full" />
        <div>
          <h2 className="text-2xl font-bold">{profileData.fullName}</h2>
          <p className="text-gray-700">@{profileData.username}</p>
          <p className="text-gray-700">Subscribers: {profileData.subscribers}</p>
        </div>
      </div>
      <div className="mt-6">
        <h3 className="text-xl font-semibold mb-4">Your Videos</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {profileData.videos.map((video) => (
            <div key={video.id} className="border p-4 rounded-md">
              <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover rounded-md" />
              <h4 className="mt-2 font-semibold">{video.title}</h4>
              <p className="text-gray-700">{video.description}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
