import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Context } from "../context/contextApi";
const LikedVideosPage = () => {
    // Assuming your context provides a `user` object with `id` field
    const [likedVideos, setLikedVideos] = useState([]);
    const [message, setMessage] = useState('');
    const[user, setUser] = useState();
    useEffect(() => {
        const fetchUserProfile = async () => {
          try {
            const response = await axios.get('http://localhost:7000/api/v1/users/profile', {
              headers: {
                Authorization: `Bearer ${localStorage.getItem('accessToken')}`,
              },
              withCredentials: true,
            });
            setUser(response.data);
          } catch (err) {
            console.error('Failed to fetch profile data', err);
          }
        };

        fetchUserProfile();
      }, []);


    useEffect(() => {
        const fetchLikedVideos = async () => {
            try {
                const response = await axios.get(`http://localhost:7000/api/v1/likes/videos/${user._id}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                    },
                    withCredentials: true
                });
              console.log(response.data.data);
                if (response.data.data && response.data.data.likedVideos) {
                    setLikedVideos(response.data.data.likedVideos);
                } else {
                    setMessage(' liked videos found');
                }
            } catch (error) {
                if (error.response) {
                    console.error('Server responded with error:', error.response.data);
                } else if (error.request) {
                    console.error('No response received from server:', error.request);
                } else {
                    console.error('Error setting up the request:', error.message);
                }
                setMessage('Failed to fetch liked videos');
            }
        };

        if (user && user.id) {
            fetchLikedVideos();
        }
    }, [user]);

    return (
        <div className="max-w-7xl mx-auto mt-10 p-6">
            <h2 className="text-2xl font-bold mb-6 text-center">Liked Videos</h2>
            {message && <p className="mb-4 text-center text-red-500">{message}</p>}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {likedVideos.length > 0 ? (
                    likedVideos.map(like => (
                        <Link key={like.video._id} to={`/video/${like.video._id}`} className="block group">
                            <div className="relative overflow-hidden rounded-md shadow-lg">
                                <img src={like.video.thumbnail} alt={like.video.title} className="w-full h-48 object-cover transition-transform duration-200 transform group-hover:scale-105" />
                                <div className="p-4">
                                    <h3 className="text-lg font-semibold text-gray-900 group-hover:text-indigo-600">{like.video.title}</h3>
                                    <p className="text-sm text-gray-600 truncate">{like.video.description}</p>
                                </div>
                            </div>
                        </Link>
                    ))
                ) : (
                    <p className="text-center col-span-full">No liked videos found</p>
                )}
            </div>
        </div>
    );
};

export default LikedVideosPage;
