import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Context } from "../context/contextApi";

const Feed = () => {
  const { user } = useContext(Context);
  const [videos, setVideos] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/v1/videos', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
          },
          withCredentials: true
        });
        setVideos(response.data.data);
      } catch (error) {
        console.error('Error fetching videos:', error);
        if (error.response && error.response.data && error.response.data.message) {
          setMessage(error.response.data.message);
        } else {
          setMessage('An error occurred while fetching videos');
        }
      }
    };

    fetchVideos();
  }, []);

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <h2 className="text-2xl font-bold mb-6 text-center">Video Feed</h2>
      {message && <p className="mb-4 text-center text-red-500">{message}</p>}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {(
          videos.map(video => (
            <div key={video._id} className="p-4 border rounded-md">
              <div className="relative pb-16/9">
                <img
                  src={video.thumbnail}
                  alt={video.title}
                  className="absolute top-0 left-0 w-full h-full object-cover"
                />
              </div>
              <h3 className="text-xl font-semibold mt-2">{video.title}</h3>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Feed;
