import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Context } from "../context/contextApi";
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const Feed = () => {
  const { user } = useContext(Context);
  const [videos, setVideos] = useState([]);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/v1/videos', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
          },
          withCredentials: true
        });
        setVideos(response.data.data);
      } catch (error) {
        if (error.response) {
          // The request was made and the server responded with a status code
          console.error('Server responded with error:', error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          console.error('No response received from server:', error.request);
        } else {
          // Something happened in setting up the request that triggered an error
          console.error('Error setting up the request:', error.message);
        }
        setMessage('Failed to fetch videos');
      }
    };

    fetchVideos();
  }, []);

  return (
    <div className="max-w-6xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <h2 className="text-2xl font-bold mb-6 text-center">All Videos</h2>
      {message && <p className="mb-4 text-center text-red-500">{message}</p>}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {videos.length > 0 ? (
          videos.map(video => (
            <Link key={video._id} to={`/video/${video._id}`}>
              <div className="p-4 border rounded-md hover:shadow-lg transition-shadow duration-300">
                <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover rounded-md" />
                <h3 className="text-xl font-semibold mt-2">{video.title}</h3>
                <p className="text-gray-600">{video.description}</p>
              </div>
            </Link>
          ))
        ) : (
          <p className="text-center col-span-full">No videos found</p>
        )}
      </div>
    </div>
  );
};

export default Feed;
