import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Context } from "../context/contextApi";

const YourVideos = () => {
    const { user } = useContext(Context);
    const [videos, setVideos] = useState([]);
    const [message, setMessage] = useState('');

    const fetchVideos = async () => {
        try {
            const response = await axios.get(`http://localhost:7000/api/v1/videos/profile/${user._id}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            if (response.data.data && response.data.data.videos) {
                setVideos(response.data.data.videos);
            } else {
                setMessage('No videos found');
            }
        } catch (error) {
            console.error('Server responded with error:', error.response.data);
            setMessage('Failed to fetch videos');
        }
    };

    useEffect(() => {
        fetchVideos();
    }, [user]);

    const handleDelete = async (videoId) => {
        try {
            await axios.delete(`http://localhost:7000/api/v1/videos/${videoId}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            // Refresh videos after deletion
            fetchVideos();
        } catch (error) {
            console.error('Failed to delete video:', error);
            setMessage('Failed to delete video');
        }
    };

    return (
        <div className="max-w-7xl mx-auto mt-10 p-6">
            <h2 className="text-2xl font-bold mb-6 text-center text-white">Your Videos</h2>
            {message && <p className="mb-4 text-center text-red-500">{message}</p>}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {videos.length > 0 ? (
                    videos.map(video => (
                        <div key={video._id} className="text-white relative overflow-hidden rounded-md shadow-lg">
                            <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover transition-transform duration-200 transform group-hover:scale-105" />
                            <div className="p-4">
                                <h3 className="text-lg font-semibold text-white">{video.title}</h3>
                                <p className="text-sm text-gray-200 truncate">{video.description}</p>
                                <div className="flex justify-end mt-2">
                                    <button
                                        onClick={() => handleDelete(video._id)}
                                        className="px-4 py-2 rounded-md bg-red-500 text-white"
                                    >
                                        Delete
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="text-center col-span-full">No videos found</p>
                )}
            </div>
        </div>
    );
};

export default YourVideos;
