import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import LeftNavMenuItem from "./LeftNavMenuItem";
import { categories } from "../utils/constants";
import { Context } from "../context/contextApi";

const LeftNav = () => {
    const { mobileMenu } = useContext(Context);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const navigate = useNavigate();

    return (
        // <div
        //     className={` fixed md:relative top-0 left-0 md:w-[240px] w-[240px] h-full py-4 bg-black z-10 transition-transform duration-300 ${
        //         mobileMenu ? "translate-x-0" : "-translate-x-full"
        //     }`}
        // >
        <div
            className={`fixed md:relative   h-full py-4 bg-black z-10 transition-transform duration-300 -translate-x-full ${
                mobileMenu ? "translate-x-0" : ""
            }`}
        >

        {/* <div
            className={`md:block w-[240px] overflow-y-auto h-full py-4 bg-black absolute md:relative z-10 translate-x-[-240px] md:translate-x-0 transition-all ${
                mobileMenu ? "translate-x-0" : ""
            }`}
        > */}
            <div className="flex px-5 flex-col">
                {categories.map((item) => (
                    <React.Fragment key={item.name}>
                        <LeftNavMenuItem
                            text={item.type === "home" ? "Home" : item.name}
                            icon={item.icon}
                            action={() => {
                                setSelectedCategory(item.name);
                                navigate(`/${item.name.toLowerCase().replace(' ', '')}`);
                            }}
                            className={`${
                                selectedCategory === item.name ? "bg-white/[0.15]" : ""
                            }`}
                        />
                        {item.divider && <hr className="my-5 border-white/[0.2]" />}
                    </React.Fragment>
                ))}
                <hr className="my-5 border-white/[0.2]" />
            </div>
        </div>
    );
};

export default LeftNav;
