import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import LeftNavMenuItem from "./LeftNavMenuItem";
import { categories } from "../utils/constants";
import { Context } from "../context/contextApi";
import { CgClose } from "react-icons/cg";

const LeftNav = () => {
    const { mobileMenu, toggleMobileMenu } = useContext(Context);
    const navigate = useNavigate();

    console.log("Mobile Menu State:", mobileMenu); // Debugging statement

    return (
        <div className="fixed top-0 left-0 w-[240px] h-full overflow-y-auto py-4 bg-black z-10 transition-transform duration-300">
            <button
                onClick={toggleMobileMenu}
                className="md:hidden p-2 bg-gray-800 text-white absolute right-2 top-2 rounded"
            >
                {mobileMenu ? <CgClose /> : <div>Menu</div>}
            </button>
            <div
                className={`transform ${
                    mobileMenu ? "translate-x-0" : "-translate-x-full"
                } md:translate-x-0 transition-transform duration-300`}
            >
                <div className="flex px-5 flex-col">
                    {categories.map((item) => (
                        <React.Fragment key={item.name}>
                            <LeftNavMenuItem
                                text={item.type === "home" ? "Home" : item.name}
                                icon={item.icon}
                                action={() => {
                                    navigate(`/${item.name.toLowerCase().replace(' ', '')}`);
                                }}
                                // Add any necessary className or active states based on item
                            />
                            {item.divider && <hr className="my-5 border-white/[0.2]" />}
                        </React.Fragment>
                    ))}
                    <hr className="my-5 border-white/[0.2]" />
                </div>
            </div>
        </div>
    );
};

export default LeftNav;
