import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Context } from "../context/contextApi";

const VideoDetail = () => {
  const { videoId } = useParams();
  const { user } = useContext(Context);
  const [video, setVideo] = useState(null);
  const [comment, setComment] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get(`http://localhost:7000/api/v1/videos/${videoId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
          },
          withCredentials: true
        });
        setVideo(response.data.data);
      } catch (error) {
        setMessage('Failed to fetch video details');
      }
    };

    fetchVideo();
  }, [videoId]);

  const handleLike = async () => {
    try {
      await axios.post(`http://localhost:7000/api/v1/videos/${videoId}/like`, {}, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('accessToken')}`
        },
        withCredentials: true
      });
      setVideo({ ...video, likes: video.likes + 1 });
    } catch (error) {
      setMessage('Failed to like the video');
    }
  };

  const handleComment = async () => {
    try {
      const response = await axios.post(`http://localhost:7000/api/v1/videos/${videoId}/comments`, { comment }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('accessToken')}`
        },
        withCredentials: true
      });
      setVideo({ ...video, comments: [...video.comments, response.data.data] });
      setComment('');
    } catch (error) {
      setMessage('Failed to post comment');
    }
  };

  if (!video) return <p>Loading...</p>;

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <h2 className="text-2xl font-bold mb-6">{video.title}</h2>
      {message && <p className="mb-4 text-center text-red-500">{message}</p>}
      <video controls src={video.videoFile} className="w-full mb-4" />
      <div className="mb-4">
        <button onClick={handleLike} className="px-4 py-2 bg-blue-600 text-white rounded-md">Like ({video.likes})</button>
      </div>
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Comments</h3>
        <div className="space-y-4">
          {video.comments.map(comment => (
            <div key={comment._id} className="p-4 border rounded-md">
              <p className="font-semibold">{comment.user.name}</p>
              <p>{comment.text}</p>
            </div>
          ))}
        </div>
      </div>
      <div>
        <h3 className="text-lg font-semibold mb-2">Add a Comment</h3>
        <textarea 
          value={comment} 
          onChange={(e) => setComment(e.target.value)} 
          className="w-full p-2 border rounded-md mb-4"
        />
        <button onClick={handleComment} className="px-4 py-2 bg-green-600 text-white rounded-md">Comment</button>
      </div>
    </div>
  );
};

export default VideoDetail;
