import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import { Context } from "../context/contextApi";

const VideoPage = () => {
    const { user } = useContext(Context);
    const [videos, setVideos] = useState([]);
    const [message, setMessage] = useState('');

    console.log(user._id);

    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await axios.get(`http://localhost:7000/api/v1/videos/profile/${user._id}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                    },
                    withCredentials: true
                });
                console.log(response.data)
                if (response.data.data && response.data.data.videos) {
                    setVideos(response.data.data.videos);
                } else {
                    setMessage('No videos found');
                }
            } catch (error) {
                if (error.response) {
                    console.error('Server responded with error:', error.response.data);
                } else if (error.request) {
                    console.error('No response received from server:', error.request);
                } else {
                    console.error('Error setting up the request:', error.message);
                }
                setMessage('Failed to fetch videos');
            }
        };

        fetchVideos();
    }, [user]);

    const handleDelete = async (videoId) => {
        try {
            const response = await axios.delete(`http://localhost:7000/api/v1/videos/${videoId}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                },
                withCredentials: true
            });
            setVideos(videos.filter(video => video._id !== videoId));
            setMessage('Video deleted successfully');
        } catch (error) {
            if (error.response) {
                console.error('Server responded with error:', error.response.data);
            } else if (error.request) {
                console.error('No response received from server:', error.request);
            } else {
                console.error('Error setting up the request:', error.message);
            }
            setMessage('Failed to delete video');
        }
    };

    return (
        <div className="max-w-7xl mx-auto mt-10 p-6">
            <h2 className="text-2xl font-bold mb-6 text-center">Your Videos</h2>
            {message && <p className="mb-4 text-center text-red-500">{message}</p>}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {videos.length > 0 ? (
                    videos.map(video => (
                        <div key={video._id} className="block group">
                            <div className="relative overflow-hidden rounded-md shadow-lg">
                                <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover transition-transform duration-200 transform group-hover:scale-105" />
                                <div className="p-4">
                                    <h3 className="text-lg font-semibold text-gray-900 group-hover:text-indigo-600">{video.title}</h3>
                                    <p className="text-sm text-gray-600 truncate">{video.description}</p>
                                    <Link to={`/video/${video._id}`} className="text-indigo-600 hover:underline">View</Link>
                                    <Link ><button

                                        onClick={() => handleDelete(video._id)}
                                        className="ml-4 text-red-600 hover:underline"
                                    >
                                        Delete
                                    </button>
                                    </Link>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="text-center col-span-full">No videos found</p>
                )}
            </div>
        </div>
    );
};

export default VideoPage;
