import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
    const { userId } = useParams();
    const [videos, setVideos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchVideos = async () => {
            if (userId) {
                try {
                    const response = await axios.get(`http://localhost:7000/api/v1/videos/${userId}`, {
                        headers: {
                            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
                        },
                        withCredentials: true
                    });
                    setVideos(response.data.data || []); // Handle empty response gracefully
                } catch (error) {
                    if (error.response) {
                        // The request was made and the server responded with a status code
                        console.error('Server responded with error:', error.response.data);
                        setError('Error fetching videos: ' + error.response.data.message);
                    } else if (error.request) {
                        // The request was made but no response was received
                        console.error('No response received from server:', error.request);
                        setError('No response received from server');
                    } else {
                        // Something happened in setting up the request that triggered an error
                        console.error('Error setting up the request:', error.message);
                        setError('Error setting up the request: ' + error.message);
                    }
                } finally {
                    setLoading(false);
                }
            }
        };

        fetchVideos();
    }, [userId]);



    return (
        <div>
            <h1>User Videos</h1>
            {videos.length === 0 ? (
                <p>No videos found for this user.</p>
            ) : (
                <ul>
                    {videos.map(video => (
                        <li key={video._id}>
                            <h2>{video.title}</h2>
                            <video width="320" height="240" controls>
                                <source src={video.url} type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ProfilePage;
