import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Context } from "../context/contextApi";

const VideoDetail = () => {
  const { videoId } = useParams();
  const { user } = useContext(Context);
  const [video, setVideo] = useState(null);
  const [comment, setComment] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get(`http://localhost:7000/api/v1/videos/${videoId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
          },
          withCredentials: true
        });
        setVideo(response.data.data);
      } catch (error) {
        setMessage('Failed to fetch video details');
      }
    };

    fetchVideo();
  }, [videoId]);

  const handleLike = async () => {
    try {
      await axios.post(`http://localhost:7000/api/v1/likes/toggle/${videoId}`, {}, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('accessToken')}`
        },
        withCredentials: true
      });
      setVideo({ ...video, likes: video.likes + 1 });
    } catch (error) {
      setMessage('Failed to like the video');
    }
  };

  const handleComment = async () => {
    try {
      const response = await axios.post(`http://localhost:7000/api/v1/comments/${videoId}`, { comment }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('accessToken')}`
        },
        withCredentials: true
      });
      setVideo({ ...video, comments: [...video.comments, response.data.data] });
      setComment('');
    } catch (error) {
      setMessage('Failed to post comment');
    }
  };

  if (!video) return <p>Loading...</p>;

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <h2 className="text-2xl font-bold mb-6">{video.title}</h2>
      {message && <p className="mb-4 text-center text-red-500">{message}</p>}
      <video controls src={video.videoFile} className="w-full mb-4" />

    </div>
  );
};

export default VideoDetail;
