import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Context } from "../context/contextApi";

const Feed = () => {
  const { user } = useContext(Context);
  const [videos, setVideos] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/v1/videos', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
          },
          withCredentials: true
        });
        setVideos(response.data.data);
      } catch (error) {
        setMessage('Failed to fetch videos');
      }
    };

    fetchVideos();
  }, []);

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <h2 className="text-2xl font-bold mb-6 text-center">All Videos</h2>
      {message && <p className="mb-4 text-center text-red-500">{message}</p>}
      <div className="space-y-4">
        {videos.length > 0 ? (
          videos.map(video => (
            <div key={video._id} className="p-4 border rounded-md">
              <h3 className="text-xl font-semibold">{video.title}</h3>
              <p>{video.description}</p>
              <video controls src={video.videoFile} className="w-full mt-2" />
            </div>
          ))
        ) : (
          <p className="text-center">No videos found</p>
        )}
      </div>
    </div>
  );
};

export default Feed;
