import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Context } from "../context/contextApi";

const ProfilePage = () => {
    const [userId, setUserId] = useState(null);
    const [videos, setVideos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { user } = useContext(Context);

    useEffect(() => {
        if (user) {
            setUserId(user._id); // Use setUserId to update the state
        }
    }, [user]);

    useEffect(() => {
        const fetchVideos = async () => {
            if (userId) {
                try {
                    const response = await axios.get(`http://localhost:7000/api/v1/videos/${userId}`);
                    setVideos(response.data.data);
                } catch (err) {
                    setError(err.response ? err.response.data.message : 'Error fetching videos');
                } finally {
                    setLoading(false);
                }
            }
        };

        fetchVideos();
    }, [userId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h1>User Videos</h1>
            {videos.length === 0 ? (
                <p>No videos found for this user.</p>
            ) : (
                <ul>
                    {videos.map(video => (
                        <li key={video._id}>
                            <h2>{video.title}</h2>
                            <video width="320" height="240" controls>
                                <source src={video.url} type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ProfilePage;
