import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import LeftNavMenuItem from "./LeftNavMenuItem";
import { categories } from "../utils/constants";
import { Context } from "../context/contextApi";

const LeftNav = () => {
    const { mobileMenu, toggleMobileMenu } = useContext(Context);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const navigate = useNavigate();

    console.log("Mobile Menu State:", mobileMenu); // Debugging statement

    return (
        <div className="relative">
            <button
                onClick={toggleMobileMenu}
                className="md:hidden fixed top-2 left-2 p-2 bg-gray-800 text-white z-20">
                Toggle Menu
            </button>
            <div
                className={`fixed md:relative top-0 left-0 md:w-[240px] w-[240px] h-full overflow-y-auto py-4 bg-black z-10 transition-transform duration-300 ${
                    mobileMenu ? "translate-x-0" : "-translate-x-[240px]"
                } md:translate-x-0`}
            >
                <div className="flex px-5 flex-col">
                    {categories.map((item) => (
                        <React.Fragment key={item.name}>
                            <LeftNavMenuItem
                                text={item.type === "home" ? "Home" : item.name}
                                icon={item.icon}
                                action={() => {
                                    setSelectedCategory(item.name);
                                    navigate(`/${item.name.toLowerCase().replace(' ', '')}`);
                                }}
                                className={`${
                                    selectedCategory === item.name
                                        ? "bg-white/[0.15]"
                                        : ""
                                }`}
                            />
                            {item.divider && (
                                <hr className="my-5 border-white/[0.2]" />
                            )}
                        </React.Fragment>
                    ))}
                    <hr className="my-5 border-white/[0.2]" />
                </div>
            </div>
        </div>
    );
};

export default LeftNav;
