import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
    const { userId } = useParams();
    const [videos, setVideos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchVideos = async () => {
            const token = localStorage.getItem('accessToken'); // Retrieve the token from local storage

            // Check if the token is valid and in the correct format
            if (!token || !token.includes('.')) {
                console.error('Invalid token:', token); // Log the invalid token
                setError('Invalid token');
                setLoading(false);
                return;
            }

            console.log('Using token:', token); // Log the token being used for the request

            try {
                const response = await axios.get(`http://localhost:7000/api/v1/videos/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    } 
                });

                console.log('API response:', response.data); // Log the API response

                // Adjust this condition based on the actual API response
                if (Array.isArray(response.data.videos)) {
                    setVideos(response.data.videos);
                } else if (response.data && response.data.data && Array.isArray(response.data.data.videos)) {
                    // Adjust based on the actual API response structure
                    setVideos(response.data.data.videos);
                } else {
                    console.error('Unexpected API response format:', response.data);
                    setError('Unexpected API response format');
                }
            } catch (error) {
                if (error.response) {
                    console.error('Server responded with error:', error.response.data);
                    setError('Error fetching videos: ' + error.response.data.message);
                } else if (error.request) {
                    console.error('No response received from server:', error.request);
                    setError('No response received from server');
                } else {
                    console.error('Error setting up the request:', error.message);
                    setError('Error setting up the request: ' + error.message);
                }
            } finally {
                setLoading(false);
            }
        };

        fetchVideos();
    }, [userId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="max-w-7xl mx-auto mt-10 p-6">
          <h2 className="text-2xl font-bold mb-6 text-center">All Videos</h2>
          {message && <p className="mb-4 text-center text-red-500">{message}</p>}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {videos.length > 0 ? (
              videos.map(video => (
                <Link key={video._id} to={`/video/${video._id}`} className="block group">
                  <div className="relative overflow-hidden rounded-md shadow-lg">
                    <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover transition-transform duration-200 transform group-hover:scale-105" />
                    <div className="p-4">
                      <h3 className="text-lg font-semibold text-gray-900 group-hover:text-indigo-600">{video.title}</h3>
                      <p className="text-sm text-gray-600 truncate">{video.description}</p>
                    </div>
                  </div>
                </Link>
              ))
            ) : (
              <p className="text-center col-span-full">No videos found</p>
            )}
          </div>
        </div>
      );
};

export default ProfilePage;
