import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UpdateAvatar = () => {
  const [avatar, setAvatar] = useState(null);
  const [message, setMessage] = useState('');
  const [currentAvatar, setCurrentAvatar] = useState(null); // State to hold current avatar URL
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCurrentAvatar = async () => {
      try {
        const response = await axios.get('http://localhost:7000/api/v1/users/current/avatar', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`
          },
          withCredentials: true
        });
        setCurrentAvatar(response.data.avatar); // Assuming your API returns avatar URL
      } catch (error) {
        console.error('Failed to fetch current avatar:', error.message);
      }
    };

    fetchCurrentAvatar();
  }, []);

  const handleAvatarChange = (e) => {
    setAvatar(e.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('avatar', avatar);

    try {
      await axios.put(
        'http://localhost:7000/api/v1/users/avatar',
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('accessToken')}`,
            'Content-Type': 'multipart/form-data'
          },
          withCredentials: true,
        }
      );
      setMessage('Avatar updated successfully');
      // Optionally, you can update the current avatar state here after successful upload
    } catch (error) {
      setMessage('Failed to update avatar');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-md rounded-md">
      <h2 className="text-2xl font-bold mb-6 text-center">Update Avatar</h2>
      {message && <p className="mb-4 text-center text-red-500">{message}</p>}
      {currentAvatar && (
        <div className="flex justify-center mb-4">
          <img src={currentAvatar} alt="Current Avatar" className="w-20 h-20 rounded-full" />
        </div>
      )}
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">Upload Avatar</label>
          <input
            type="file"
            onChange={handleAvatarChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-indigo-500 text-white py-2 px-4 rounded-md shadow-sm hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
        >
          Update Avatar
        </button>
      </form>
    </div>
  );
};

export default UpdateAvatar;
