import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

const ProfilePage = () => {
    const { userId } = useParams();
    const [videos, setVideos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const token = localStorage.getItem('accessToken');

                if (!token || !token.includes('.')) {
                    throw new Error('Invalid token format');
                }

                const response = await axios.get(`http://localhost:7000/api/v1/videos/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    },
                    withCredentials: true // Adjust as needed based on your server configuration
                });

                console.log('API Response:', response.data);

                if (response.data.success) {
                    setVideos(response.data.data || []); // Ensure videos is an array or fallback to empty array
                } else {
                    throw new Error(response.data.message || 'Failed to fetch videos');
                }
            } catch (error) {
                console.error('Fetch videos error:', error.message);
                setError(error.message || 'Failed to fetch videos');
            } finally {
                setLoading(false);
            }
        };

        fetchVideos();
    }, [userId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="max-w-7xl mx-auto mt-10 p-6">
        <h2 className="text-2xl font-bold mb-6 text-center">All Videos</h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {videos.length > 0 ? (
            videos.map(video => (
              <Link key={video._id} to={`/video/${video._id}`} className="block group">
                <div className="relative overflow-hidden rounded-md shadow-lg">
                  <img src={video.thumbnail} alt={video.title} className="w-full h-48 object-cover transition-transform duration-200 transform group-hover:scale-105" />
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-gray-900 group-hover:text-indigo-600">{video.title}</h3>
                    <p className="text-sm text-gray-600 truncate">{video.description}</p>
                  </div>
                </div>
              </Link>
            ))
          ) : (
            <p className="text-center col-span-full">No videos found</p>
          )}
        </div>
      </div>
    );
};

export default ProfilePage;
